import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import { Car, User, MapPin, DollarSign, Phone, X, Calendar, Clock, Search, MessageCircle, Send } from 'lucide-react';

// Socket connection
const socket = io('http://localhost:5000');

export default function SyncWay() {
  const [userType, setUserType] = useState(null);
  const [userName, setUserName] = useState('');
  const [userPhone, setUserPhone] = useState('');
  const [userId, setUserId] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activeTab, setActiveTab] = useState('request');

  // Search states
  const [searchMyRides, setSearchMyRides] = useState('');
  const [searchBrowse, setSearchBrowse] = useState('');
  const [searchAvailable, setSearchAvailable] = useState('');
  const [searchClaimed, setSearchClaimed] = useState('');
  const [searchMyOffers, setSearchMyOffers] = useState('');

  // Ride request form
  const [startLocation, setStartLocation] = useState('');
  const [endLocation, setEndLocation] = useState('');
  const [distance, setDistance] = useState('');
  const [rideType, setRideType] = useState('pickup');

  // Driver offer form
  const [offerStart, setOfferStart] = useState('');
  const [offerEnd, setOfferEnd] = useState('');
  const [offerDate, setOfferDate] = useState('');
  const [offerTime, setOfferTime] = useState('');

  // Data states
  const [rides, setRides] = useState([]);
  const [offers, setOffers] = useState([]);
  const [chatRideId, setChatRideId] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  const chatEndRef = useRef(null);

  // Socket effects
  useEffect(() => {
    socket.on('newRideAvailable', (ride) => {
      if (userType === 'driver') {
        setRides(prev => [ride, ...prev]);
      }
    });

    socket.on(`ride_${chatRideId}_claimed`, (ride) => {
      if (userType === 'user') {
        alert(`Your ride has been claimed by ${ride.claimedBy}!`);
        setRides(prev => prev.map(r => r._id === ride._id ? ride : r));
      }
    });

    socket.on('newMessage', (message) => {
      if (message.rideId === chatRideId) {
        setMessages(prev => [...prev, message]);
      }
    });

    return () => {
      socket.off('newRideAvailable');
      socket.off(`ride_${chatRideId}_claimed`);
      socket.off('newMessage');
    };
  }, [userType, chatRideId]);

  // Scroll to bottom of chat
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const calculateFare = (dist) => {
    const fare = dist * 2;
    return Math.max(fare, 10);
  };

  const handleLogin = async (type) => {
    if (userName.trim() && userPhone.trim()) {
      try {
        const response = await fetch('http://localhost:5000/api/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ 
            name: userName, 
            phone: userPhone, 
            userType: type 
          })
        });

        const data = await response.json();

        if (data.success) {
          setUserType(type);
          setIsLoggedIn(true);
          setUserId(data.user.id);

          // Load initial data
          if (type === 'user') {
            loadUserRides();
            loadOffers();
          } else {
            loadAvailableRides();
            loadDriverOffers();
          }
        }
      } catch (error) {
        console.error('Login error:', error);
      }
    }
  };

  const loadUserRides = async () => {
    // In real app, filter by user ID
    // For demo, we'll use localStorage
    const savedRides = JSON.parse(localStorage.getItem('rides') || '[]');
    setRides(savedRides.filter(r => r.userName === userName));
  };

  const loadAvailableRides = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/rides/available');
      const data = await response.json();
      setRides(data);
    } catch (error) {
      console.error('Error loading rides:', error);
    }
  };

  const loadOffers = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/offers');
      const data = await response.json();
      setOffers(data);
    } catch (error) {
      console.error('Error loading offers:', error);
    }
  };

  const loadDriverOffers = async () => {
    const savedOffers = JSON.parse(localStorage.getItem('offers') || '[]');
    setOffers(savedOffers.filter(o => o.driverName === userName));
  };

  const handleRequestRide = async () => {
    if (startLocation && endLocation && distance) {
      const dist = parseFloat(distance);
      const fare = calculateFare(dist);

      const rideData = {
        userId,
        userName,
        userPhone,
        startLocation,
        endLocation,
        distance: dist,
        fare: fare.toFixed(2),
        rideType,
        claimed: false
      };

      try {
        const response = await fetch('http://localhost:5000/api/rides', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(rideData)
        });

        const data = await response.json();

        if (data.success) {
          setRides(prev => [data.ride, ...prev]);
          setStartLocation('');
          setEndLocation('');
          setDistance('');
        }
      } catch (error) {
        console.error('Error creating ride:', error);
      }
    }
  };

  const handleOfferRide = () => {
    if (offerStart && offerEnd && offerDate && offerTime) {
      const newOffer = {
        driverId: userId,
        driverName: userName,
        driverPhone: userPhone,
        startLocation: offerStart,
        endLocation: offerEnd,
        date: offerDate,
        time: offerTime
      };

      // Save to localStorage (replace with API call)
      const savedOffers = JSON.parse(localStorage.getItem('offers') || '[]');
      savedOffers.push({ ...newOffer, id: Date.now() });
      localStorage.setItem('offers', JSON.stringify(savedOffers));

      setOffers(prev => [...prev, newOffer]);
      setOfferStart('');
      setOfferEnd('');
      setOfferDate('');
      setOfferTime('');
    }
  };

  const handleClaimRide = async (rideId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/rides/${rideId}/claim`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          claimedBy: userName,
          claimedDriverPhone: userPhone
        })
      });

      const data = await response.json();

      if (data.success) {
        setRides(prev => prev.map(ride => 
          ride._id === rideId ? data.ride : ride
        ));
      }
    } catch (error) {
      console.error('Error claiming ride:', error);
    }
  };

  const handleUnclaimRide = (rideId) => {
    setRides(rides.map(ride => 
      ride.id === rideId 
        ? { ...ride, claimed: false, claimedBy: null, claimedDriverPhone: null }
        : ride
    ));
  };

  const handleDeleteRide = (rideId) => {
    setRides(rides.filter(ride => ride.id !== rideId));
  };

  const handleDeleteOffer = (offerId) => {
    setOffers(offers.filter(offer => offer.id !== offerId));
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserType(null);
    setUserName('');
    setUserPhone('');
    setUserId(null);
    setActiveTab('request');
    setRides([]);
    setOffers([]);
    socket.disconnect();
  };

  // Chat Functions
  const openChat = (rideId) => {
    setChatRideId(rideId);
    socket.emit('joinRide', rideId);

    // Load chat history
    fetch(`http://localhost:5000/api/chats/${rideId}`)
      .then(res => res.json())
      .then(data => setMessages(data))
      .catch(err => console.error('Error loading chat:', err));
  };

  const closeChat = () => {
    setChatRideId(null);
    setMessages([]);
  };

  const sendMessage = () => {
    if (newMessage.trim() && chatRideId) {
      socket.emit('sendMessage', {
        rideId: chatRideId,
        senderId: userId,
        senderName: userName,
        senderType: userType,
        message: newMessage.trim()
      });
      setNewMessage('');
    }
  };

  // Filter functions
  const filterRides = (ridesList, searchTerm) => {
    if (!searchTerm) return ridesList;
    const term = searchTerm.toLowerCase();
    return ridesList.filter(ride => 
      ride.startLocation.toLowerCase().includes(term) ||
      ride.endLocation.toLowerCase().includes(term) ||
      (ride.claimedBy && ride.claimedBy.toLowerCase().includes(term)) ||
      (ride.userName && ride.userName.toLowerCase().includes(term))
    );
  };

  const filterOffers = (offersList, searchTerm) => {
    if (!searchTerm) return offersList;
    const term = searchTerm.toLowerCase();
    return offersList.filter(offer => 
      offer.startLocation.toLowerCase().includes(term) ||
      offer.endLocation.toLowerCase().includes(term) ||
      offer.driverName.toLowerCase().includes(term) ||
      (offer.date && offer.date.includes(term))
    );
  };

  // Chat Modal Component
  const ChatModal = () => (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg w-full max-w-md">
        <div className="flex justify-between items-center p-4 border-b">
          <h3 className="font-bold">Chat</h3>
          <button onClick={closeChat} className="text-gray-500 hover:text-gray-700">
            <X size={20} />
          </button>
        </div>

        <div className="p-4 h-64 overflow-y-auto">
          {messages.map((msg, index) => (
            <div key={index} className={`mb-3 ${msg.senderId === userId ? 'text-right' : ''}`}>
              <div className={`inline-block p-2 rounded-lg ${msg.senderId === userId ? 'bg-blue-100' : 'bg-gray-100'}`}>
                <p className="text-sm font-medium">{msg.senderName}</p>
                <p>{msg.message}</p>
                <p className="text-xs text-gray-500 mt-1">
                  {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                </p>
              </div>
            </div>
          ))}
          <div ref={chatEndRef} />
        </div>

        <div className="p-4 border-t flex gap-2">
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
            placeholder="Type your message..."
            className="flex-1 border rounded-lg p-2"
          />
          <button
            onClick={sendMessage}
            className="bg-blue-500 text-white p-2 rounded-lg hover:bg-blue-600"
          >
            <Send size={20} />
          </button>
        </div>
      </div>
    </div>
  );

  // Login Screen
  if (!isLoggedIn) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center p-4">
        <div className="bg-white rounded-lg shadow-2xl p-8 max-w-md w-full">
          <div className="text-center mb-8">
            <h1 className="text-4xl font-bold text-gray-800 mb-2">SyncWay</h1>
            <p className="text-gray-600">Simple Ride Sharing</p>
          </div>

          <div className="space-y-4 mb-6">
            <input
              type="text"
              placeholder="Your Name"
              value={userName}
              onChange={(e) => setUserName(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <input
              type="tel"
              placeholder="Phone Number"
              value={userPhone}
              onChange={(e) => setUserPhone(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <button
              onClick={() => handleLogin('user')}
              className="bg-blue-500 text-white py-3 rounded-lg font-semibold hover:bg-blue-600 transition flex items-center justify-center gap-2"
            >
              <User size={20} />
              User
            </button>
            <button
              onClick={() => handleLogin('driver')}
              className="bg-purple-500 text-white py-3 rounded-lg font-semibold hover:bg-purple-600 transition flex items-center justify-center gap-2"
            >
              <Car size={20} />
              Driver
            </button>
          </div>
        </div>
      </div>
    );
  }

  // User Dashboard
  if (userType === 'user') {
    return (
      <div className="min-h-screen bg-gray-100">
        <div className="bg-blue-500 text-white p-4 shadow-lg">
          <div className="max-w-4xl mx-auto flex justify-between items-center">
            <div>
              <h1 className="text-2xl font-bold">SyncWay</h1>
              <p className="text-sm">Welcome, {userName}</p>
            </div>
            <button
              onClick={handleLogout}
              className="bg-white text-blue-500 px-4 py-2 rounded-lg font-semibold hover:bg-gray-100"
            >
              Logout
            </button>
          </div>
        </div>

        <div className="max-w-4xl mx-auto p-4">
          {/* Tabs */}
          <div className="bg-white rounded-lg shadow-md mb-4">
            <div className="flex border-b border-gray-200">
              <button
                onClick={() => setActiveTab('request')}
                className={`flex-1 py-3 px-4 font-semibold ${activeTab === 'request' ? 'bg-blue-500 text-white' : 'text-gray-600 hover:bg-gray-50'}`}
              >
                Request Ride
              </button>
              <button
                onClick={() => setActiveTab('myrides')}
                className={`flex-1 py-3 px-4 font-semibold ${activeTab === 'myrides' ? 'bg-blue-500 text-white' : 'text-gray-600 hover:bg-gray-50'}`}
              >
                My Rides
              </button>
              <button
                onClick={() => setActiveTab('browse')}
                className={`flex-1 py-3 px-4 font-semibold ${activeTab === 'browse' ? 'bg-blue-500 text-white' : 'text-gray-600 hover:bg-gray-50'}`}
              >
                Browse Offers
              </button>
            </div>
          </div>

          {/* Request Ride Tab */}
          {activeTab === 'request' && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-bold mb-4">Request a Ride</h2>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium mb-1">Start Location</label>
                  <input
                    type="text"
                    value={startLocation}
                    onChange={(e) => setStartLocation(e.target.value)}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1">End Location</label>
                  <input
                    type="text"
                    value={endLocation}
                    onChange={(e) => setEndLocation(e.target.value)}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1">Distance (miles)</label>
                  <input
                    type="number"
                    step="0.1"
                    value={distance}
                    onChange={(e) => setDistance(e.target.value)}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                  {distance && (
                    <p className="text-sm text-gray-600 mt-1">
                      Estimated fare: ${calculateFare(parseFloat(distance)).toFixed(2)}
                    </p>
                  )}
                </div>

                <div>
                  <label className="block text-sm font-medium mb-1">Type</label>
                  <select
                    value={rideType}
                    onChange={(e) => setRideType(e.target.value)}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="pickup">Pickup</option>
                    <option value="ride">Ask Ride</option>
                  </select>
                </div>

                <button
                  onClick={handleRequestRide}
                  className="w-full bg-blue-500 text-white py-3 rounded-lg font-semibold hover:bg-blue-600 transition"
                >
                  Request Ride
                </button>
              </div>
            </div>
          )}

          {/* My Rides Tab */}
          {activeTab === 'myrides' && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-bold mb-4">My Rides</h2>

              {/* Search Bar */}
              <div className="mb-4 relative">
                <Search className="absolute left-3 top-3 text-gray-400" size={20} />
                <input
                  type="text"
                  placeholder="Search by location or driver name..."
                  value={searchMyRides}
                  onChange={(e) => setSearchMyRides(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              {filterRides(rides.filter(r => r.userName === userName), searchMyRides).length === 0 ? (
                <p className="text-gray-500">No rides found</p>
              ) : (
                <div className="space-y-4">
                  {filterRides(rides.filter(r => r.userName === userName), searchMyRides).map(ride => (
                    <div key={ride.id || ride._id} className="border border-gray-200 rounded-lg p-4">
                      <div className="flex justify-between items-start mb-2">
                        <div className="flex-1">
                          <p className="font-semibold">{ride.startLocation}  {ride.endLocation}</p>
                          <p className="text-sm text-gray-600">{ride.distance} miles  ${ride.fare}</p>
                          <p className="text-sm text-gray-600 capitalize">{ride.rideType}</p>
                        </div>
                        <div className="flex flex-col items-end gap-2">
                          <span className={`px-3 py-1 rounded-full text-sm ${ride.claimed ? 'bg-green-100 text-green-700' : 'bg-yellow-100 text-yellow-700'}`}>
                            {ride.claimed ? 'Claimed' : 'Waiting'}
                          </span>
                          {ride.claimed && (
                            <button
                              onClick={() => openChat(ride.id || ride._id)}
                              className="bg-blue-500 text-white px-3 py-1 rounded text-sm hover:bg-blue-600 flex items-center gap-1"
                            >
                              <MessageCircle size={14} />
                              Chat
                            </button>
                          )}
                        </div>
                      </div>
                      {ride.claimed && (
                        <div className="mt-2 pt-2 border-t border-gray-200">
                          <p className="text-sm text-gray-700 mb-2">
                            <Phone size={14} className="inline mr-1" />
                            Driver: {ride.claimedBy} - {ride.claimedDriverPhone}
                          </p>
                          <button
                            onClick={() => handleUnclaimRide(ride.id || ride._id)}
                            className="bg-orange-500 text-white px-3 py-1 rounded text-sm hover:bg-orange-600 mr-2"
                          >
                            Unclaim
                          </button>
                        </div>
                      )}
                      <button
                        onClick={() => handleDeleteRide(ride.id || ride._id)}
                        className="mt-2 bg-red-500 text-white px-3 py-1 rounded text-sm hover:bg-red-600 flex items-center gap-1"
                      >
                        <X size={14} />
                        Delete
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {/* Browse Offers Tab */}
          {activeTab === 'browse' && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-bold mb-4">Available Driver Offers</h2>

              {/* Search Bar */}
              <div className="mb-4 relative">
                <Search className="absolute left-3 top-3 text-gray-400" size={20} />
                <input
                  type="text"
                  placeholder="Search by location, date, or driver name..."
                  value={searchBrowse}
                  onChange={(e) => setSearchBrowse(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              {filterOffers(offers, searchBrowse).length === 0 ? (
                <p className="text-gray-500">No driver offers found</p>
              ) : (
                <div className="space-y-4">
                  {filterOffers(offers, searchBrowse).map(offer => (
                    <div key={offer.id || offer._id} className="border border-gray-200 rounded-lg p-4 bg-purple-50">
                      <p className="font-semibold">{offer.startLocation}  {offer.endLocation}</p>
                      <p className="text-sm text-gray-600 mt-1">
                        <Calendar size={14} className="inline mr-1" />
                        {offer.date} at {offer.time}
                      </p>
                      <p className="text-sm text-gray-700 mt-2">
                        <Phone size={14} className="inline mr-1" />
                        Driver: {offer.driverName} - {offer.driverPhone}
                      </p>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>

        {/* Chat Modal */}
        {chatRideId && <ChatModal />}
      </div>
    );
  }

  // Driver Dashboard
  return (
    <div className="min-h-screen bg-gray-100">
      <div className="bg-purple-500 text-white p-4 shadow-lg">
        <div className="max-w-4xl mx-auto flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold">SyncWay Driver</h1>
            <p className="text-sm">Welcome, {userName}</p>
          </div>
          <button
            onClick={handleLogout}
            className="bg-white text-purple-500 px-4 py-2 rounded-lg font-semibold hover:bg-gray-100"
          >
            Logout
          </button>
        </div>
      </div>

      <div className="max-w-4xl mx-auto p-4">
        {/* Tabs */}
        <div className="bg-white rounded-lg shadow-md mb-4">
          <div className="flex border-b border-gray-200">
            <button
              onClick={() => setActiveTab('available')}
              className={`flex-1 py-3 px-4 font-semibold ${activeTab === 'available' ? 'bg-purple-500 text-white' : 'text-gray-600 hover:bg-gray-50'}`}
            >
              Available Rides
            </button>
            <button
              onClick={() => setActiveTab('claimed')}
              className={`flex-1 py-3 px-4 font-semibold ${activeTab === 'claimed' ? 'bg-purple-500 text-white' : 'text-gray-600 hover:bg-gray-50'}`}
            >
              My Claims
            </button>
            <button
              onClick={() => setActiveTab('offer')}
              className={`flex-1 py-3 px-4 font-semibold ${activeTab === 'offer' ? 'bg-purple-500 text-white' : 'text-gray-600 hover:bg-gray-50'}`}
            >
              Offer Ride
            </button>
            <button
              onClick={() => setActiveTab('myoffers')}
              className={`flex-1 py-3 px-4 font-semibold ${activeTab === 'myoffers' ? 'bg-purple-500 text-white' : 'text-gray-600 hover:bg-gray-50'}`}
            >
              My Offers
            </button>
          </div>
        </div>

        {/* Available Rides Tab */}
        {activeTab === 'available' && (
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-xl font-bold mb-4">Available Rides</h2>

            {/* Search Bar */}
            <div className="mb-4 relative">
              <Search className="absolute left-3 top-3 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Search by location or user name..."
                value={searchAvailable}
                onChange={(e) => setSearchAvailable(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>

            {filterRides(rides.filter(r => !r.claimed), searchAvailable).length === 0 ? (
              <p className="text-gray-500">No rides found</p>
            ) : (
              <div className="space-y-4">
                {filterRides(rides.filter(r => !r.claimed), searchAvailable).map(ride => (
                  <div key={ride.id || ride._id} className="border border-gray-200 rounded-lg p-4">
                    <div className="flex justify-between items-start mb-2">
                      <div className="flex-1">
                        <p className="font-semibold">{ride.startLocation}  {ride.endLocation}</p>
                        <p className="text-sm text-gray-600">{ride.distance} miles  ${ride.fare}</p>
                        <p className="text-sm text-gray-600 capitalize">{ride.rideType}</p>
                        <p className="text-sm text-gray-500">User: {ride.userName}</p>
                      </div>
                      <button
                        onClick={() => handleClaimRide(ride._id || ride.id)}
                        className="bg-purple-500 text-white px-4 py-2 rounded-lg font-semibold hover:bg-purple-600"
                      >
                        Claim
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {/* Claimed Rides Tab */}
        {activeTab === 'claimed' && (
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-xl font-bold mb-4">Your Claimed Rides</h2>

            {/* Search Bar */}
            <div className="mb-4 relative">
              <Search className="absolute left-3 top-3 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Search by location or user name..."
                value={searchClaimed}
                onChange={(e) => setSearchClaimed(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>

            {filterRides(rides.filter(r => r.claimedBy === userName), searchClaimed).length === 0 ? (
              <p className="text-gray-500">No claimed rides found</p>
            ) : (
              <div className="space-y-4">
                {filterRides(rides.filter(r => r.claimedBy === userName), searchClaimed).map(ride => (
                  <div key={ride.id || ride._id} className="border border-gray-200 rounded-lg p-4 bg-green-50">
                    <div className="flex justify-between items-start mb-2">
                      <div className="flex-1">
                        <p className="font-semibold">{ride.startLocation}  {ride.endLocation}</p>
                        <p className="text-sm text-gray-600">{ride.distance} miles  ${ride.fare}</p>
                        <p className="text-sm text-gray-600 capitalize">{ride.rideType}</p>
                        <p className="text-sm text-gray-700 mt-2">
                          <Phone size={14} className="inline mr-1" />
                          User: {ride.userName} - {ride.userPhone}
                        </p>
                      </div>
                      <div className="flex flex-col gap-2">
                        <button
                          onClick={() => openChat(ride.id || ride._id)}
                          className="bg-blue-500 text-white px-3 py-1 rounded text-sm hover:bg-blue-600 flex items-center gap-1"
                        >
                          <MessageCircle size={14} />
                          Chat
                        </button>
                        <button
                          onClick={() => handleUnclaimRide(ride.id || ride._id)}
                          className="bg-red-500 text-white px-3 py-1 rounded text-sm hover:bg-red-600 flex items-center gap-1"
                        >
                          <X size={14} />
                          Unclaim
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {/* Offer Ride Tab */}
        {activeTab === 'offer' && (
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-xl font-bold mb-4">Offer a Ride</h2>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-1">Start Location</label>
                <input
                  type="text"
                  value={offerStart}
                  onChange={(e) => setOfferStart(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">End Location</label>
                <input
                  type="text"
                  value={offerEnd}
                  onChange={(e) => setOfferEnd(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Date</label>
                <input
                  type="date"
                  value={offerDate}
                  onChange={(e) => setOfferDate(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-1">Time</label>
                <input
                  type="time"
                  value={offerTime}
                  onChange={(e) => setOfferTime(e.target.value)}
                  className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                />
              </div>

              <button
                onClick={handleOfferRide}
                className="w-full bg-purple-500 text-white py-3 rounded-lg font-semibold hover:bg-purple-600 transition"
              >
                Post Offer
              </button>
            </div>
          </div>
        )}

        {/* My Offers Tab */}
        {activeTab === 'myoffers' && (
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-xl font-bold mb-4">My Offers</h2>

            {/* Search Bar */}
            <div className="mb-4 relative">
              <Search className="absolute left-3 top-3 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Search by location or date..."
                value={searchMyOffers}
                onChange={(e) => setSearchMyOffers(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>

            {filterOffers(offers.filter(o => o.driverName === userName), searchMyOffers).length === 0 ? (
              <p className="text-gray-500">No offers found</p>
            ) : (
              <div className="space-y-4">
                {filterOffers(offers.filter(o => o.driverName === userName), searchMyOffers).map(offer => (
                  <div key={offer.id || offer._id} className="border border-gray-200 rounded-lg p-4">
                    <p className="font-semibold">{offer.startLocation}  {offer.endLocation}</p>
                    <p className="text-sm text-gray-600 mt-1">
                      <Calendar size={14} className="inline mr-1" />
                      {offer.date} at {offer.time}
                    </p>
                    <button
                      onClick={() => handleDeleteOffer(offer.id || offer._id)}
                      className="mt-2 bg-red-500 text-white px-3 py-1 rounded text-sm hover:bg-red-600 flex items-center gap-1"
                    >
                      <X size={14} />
                      Delete
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>

      {/* Chat Modal */}
      {chatRideId && <ChatModal />}
    </div>
  );
}
