import React, { useState, useEffect, useRef } from 'react';
import { 
  Car, User, MapPin, Phone, X, Calendar, Clock, Search, 
  Navigation, Mail, MessageCircle, Star, DollarSign, 
  CheckCircle, AlertCircle, Users, LogOut, Eye, EyeOff
} from 'lucide-react';
import io from 'socket.io-client';

const API_URL = 'http://localhost:5000/api';
const socket = io('http://localhost:5000');

function App() {
  const [user, setUser] = useState(null);
  const [activeTab, setActiveTab] = useState('request');
  const [notification, setNotification] = useState(null);

  // Login states
  const [loginData, setLoginData] = useState({
    name: '',
    phone: '',
    email: '',
    userType: 'user',
    showPassword: false
  });

  // Ride states
  const [startLocation, setStartLocation] = useState('');
  const [endLocation, setEndLocation] = useState('');
  const [distance, setDistance] = useState('');
  const [fare, setFare] = useState('');
  const [rideType, setRideType] = useState('standard');

  // Data states
  const [availableRides, setAvailableRides] = useState([]);
  const [myRides, setMyRides] = useState([]);
  const [driverRides, setDriverRides] = useState([]);
  const [onlineUsers, setOnlineUsers] = useState({ online: 0, total: 0 });

  // Chat states
  const [activeChat, setActiveChat] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [typingUser, setTypingUser] = useState('');

  const chatEndRef = useRef(null);

  // Check login
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      const userData = JSON.parse(storedUser);
      setUser(userData);
      socket.emit('userConnected', userData.id);
    }
  }, []);

  // Socket event listeners
  useEffect(() => {
    if (!user) return;

    // New ride available (for drivers)
    socket.on('newRideAvailable', (ride) => {
      if (user.userType === 'driver') {
        setAvailableRides(prev => [ride, ...prev]);
        showNotification(`New ride available: ${ride.startLocation} to ${ride.endLocation}`, 'info');
      }
    });

    // Ride claimed notification (for users)
    socket.on(`rideClaimed_${user.id}`, (ride) => {
      setMyRides(prev => prev.map(r => 
        r._id === ride._id ? ride : r
      ));
      showNotification(`Your ride has been claimed by ${ride.claimedDriverName}`, 'success');
    });

    // Ride removed from available list
    socket.on('rideRemovedFromAvailable', (rideId) => {
      setAvailableRides(prev => prev.filter(r => r._id !== rideId));
    });

    // Ride status updated
    socket.on('rideStatusUpdated', (ride) => {
      if (user.userType === 'user') {
        setMyRides(prev => prev.map(r => 
          r._id === ride._id ? ride : r
        ));
      } else {
        setDriverRides(prev => prev.map(r => 
          r._id === ride._id ? ride : r
        ));
      }
    });

    // New chat message
    socket.on('newMessage', (message) => {
      if (activeChat && activeChat.rideId === message.rideId) {
        setChatMessages(prev => [...prev, message]);
        socket.emit('markAsRead', {
          rideId: message.rideId,
          userId: user.id
        });
      }
      showNotification(`New message from ${message.senderName}`, 'info');
    });

    // Typing indicator
    socket.on('userTyping', (data) => {
      if (activeChat) {
        setIsTyping(true);
        setTypingUser(data.userName);
        setTimeout(() => setIsTyping(false), 1000);
      }
    });

    // Online users update
    socket.on('onlineUsersUpdate', (data) => {
      setOnlineUsers(prev => ({ ...prev, online: data.onlineCount }));
    });

    // Load initial online count
    fetchOnlineUsers();

    return () => {
      socket.off('newRideAvailable');
      socket.off(`rideClaimed_${user.id}`);
      socket.off('rideRemovedFromAvailable');
      socket.off('rideStatusUpdated');
      socket.off('newMessage');
      socket.off('userTyping');
      socket.off('onlineUsersUpdate');
    };
  }, [user, activeChat]);

  // Scroll to bottom of chat
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatMessages]);

  const fetchOnlineUsers = async () => {
    try {
      const response = await fetch(`${API_URL}/users/online`);
      const data = await response.json();
      if (data.success) {
        setOnlineUsers({ online: data.onlineCount, total: data.totalUsers });
      }
    } catch (error) {
      console.error('Error fetching online users:', error);
    }
  };

  const handleLogin = async () => {
    // Validate required fields
    if (!loginData.name.trim()) {
      showNotification('Please enter your name', 'error');
      return;
    }

    if (!loginData.phone.trim()) {
      showNotification('Please enter your phone number', 'error');
      return;
    }

    // Phone validation (basic)
    const phoneRegex = /^[0-9]{10}$/;
    if (!phoneRegex.test(loginData.phone.replace(/\D/g, ''))) {
      showNotification('Please enter a valid 10-digit phone number', 'error');
      return;
    }

    try {
      const response = await fetch(`${API_URL}/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name: loginData.name,
          phone: loginData.phone,
          email: loginData.email,
          userType: loginData.userType
        })
      });

      const data = await response.json();
      if (data.success) {
        setUser(data.user);
        localStorage.setItem('user', JSON.stringify(data.user));
        socket.emit('userConnected', data.user.id);
        loadInitialData(data.user);
        showNotification(`Welcome ${data.user.name}!`, 'success');
      } else {
        showNotification(data.error || 'Login failed', 'error');
      }
    } catch (error) {
      showNotification('Network error. Please try again.', 'error');
    }
  };

  const loadInitialData = async (currentUser) => {
    if (currentUser.userType === 'user') {
      loadMyRides(currentUser.id);
      loadAvailableRides();
    } else {
      loadDriverRides(currentUser.id);
      loadAvailableRides();
    }
  };

  const loadMyRides = async (userId) => {
    try {
      const response = await fetch(`${API_URL}/rides/user/${userId}`);
      const data = await response.json();
      if (data.success) setMyRides(data.rides || []);
    } catch (error) {
      console.error('Error loading my rides:', error);
    }
  };

  const loadDriverRides = async (driverId) => {
    try {
      const response = await fetch(`${API_URL}/rides/driver/${driverId}`);
      const data = await response.json();
      if (data.success) setDriverRides(data.rides || []);
    } catch (error) {
      console.error('Error loading driver rides:', error);
    }
  };

  const loadAvailableRides = async () => {
    try {
      const response = await fetch(`${API_URL}/rides/available`);
      const data = await response.json();
      if (data.success) setAvailableRides(data.rides || []);
    } catch (error) {
      console.error('Error loading available rides:', error);
    }
  };

  const handleRequestRide = async () => {
    if (!startLocation || !endLocation) {
      showNotification('Please enter both locations', 'error');
      return;
    }

    const rideData = {
      userId: user.id,
      userName: user.name,
      userPhone: user.phone,
      userEmail: user.email,
      startLocation,
      endLocation,
      distance: parseFloat(distance) || 5,
      fare: parseFloat(fare) || 15,
      rideType,
      startLat: 0,
      startLng: 0,
      endLat: 0,
      endLng: 0
    };

    try {
      const response = await fetch(`${API_URL}/rides`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(rideData)
      });

      const data = await response.json();
      if (data.success) {
        setMyRides(prev => [data.ride, ...prev]);
        setStartLocation('');
        setEndLocation('');
        setDistance('');
        setFare('');
        showNotification('Ride requested successfully!', 'success');
        setActiveTab('myrides');
      }
    } catch (error) {
      showNotification('Failed to request ride', 'error');
    }
  };

  const handleClaimRide = async (ride) => {
    try {
      const response = await fetch(`${API_URL}/rides/${ride._id}/claim`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          claimedBy: user.id,
          claimedDriverName: user.name,
          claimedDriverPhone: user.phone,
          claimedDriverEmail: user.email
        })
      });

      const data = await response.json();
      if (data.success) {
        setDriverRides(prev => [data.ride, ...prev]);
        showNotification('Ride claimed successfully!', 'success');

        // Open chat with user
        openChat(data.ride);
      }
    } catch (error) {
      showNotification('Failed to claim ride', 'error');
    }
  };

  const handleCancelRide = async (rideId) => {
    if (!window.confirm('Are you sure you want to cancel this ride?')) return;

    try {
      const response = await fetch(`${API_URL}/rides/${rideId}/cancel`, {
        method: 'PUT'
      });

      const data = await response.json();
      if (data.success) {
        setMyRides(prev => prev.filter(r => r._id !== rideId));
        showNotification('Ride cancelled', 'info');
      }
    } catch (error) {
      showNotification('Failed to cancel ride', 'error');
    }
  };

  const handleCompleteRide = async (rideId) => {
    try {
      const response = await fetch(`${API_URL}/rides/${rideId}/complete`, {
        method: 'PUT'
      });

      const data = await response.json();
      if (data.success) {
        setDriverRides(prev => prev.filter(r => r._id !== rideId));
        showNotification('Ride completed successfully!', 'success');
      }
    } catch (error) {
      showNotification('Failed to complete ride', 'error');
    }
  };

  const openChat = (ride) => {
    setActiveChat({
      rideId: ride._id,
      otherUser: user.userType === 'user' ? {
        name: ride.claimedDriverName,
        type: 'driver'
      } : {
        name: ride.userName,
        type: 'user'
      }
    });

    // Load chat messages
    fetchChatMessages(ride._id);

    // Join ride room
    socket.emit('joinRide', ride._id);
  };

  const fetchChatMessages = async (rideId) => {
    try {
      const response = await fetch(`${API_URL}/chat/${rideId}`);
      const data = await response.json();
      if (data.success) setChatMessages(data.messages || []);
    } catch (error) {
      console.error('Error loading chat:', error);
    }
  };

  const sendMessage = () => {
    if (!newMessage.trim() || !activeChat) return;

    const messageData = {
      rideId: activeChat.rideId,
      senderId: user.id,
      senderName: user.name,
      senderType: user.userType,
      message: newMessage.trim()
    };

    socket.emit('sendMessage', messageData);
    setNewMessage('');
  };

  const handleTyping = () => {
    if (activeChat) {
      socket.emit('typing', {
        rideId: activeChat.rideId,
        userId: user.id,
        userName: user.name
      });
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    socket.disconnect();
    showNotification('Logged out successfully', 'info');
  };

  const showNotification = (message, type = 'info') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

