import React, { useState, useEffect } from 'react';
import { Car, User, MapPin, DollarSign, Phone, X, Calendar, Clock, Search, MessageCircle, Send, Navigation, Map } from 'lucide-react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000');

function App() {
  const [userType, setUserType] = useState(null);
  const [userName, setUserName] = useState('');
  const [userPhone, setUserPhone] = useState('');
  const [userId, setUserId] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activeTab, setActiveTab] = useState('request');

  // Location states
  const [userLocation, setUserLocation] = useState({ lat: 0, lon: 0, address: '' });
  const [startLocation, setStartLocation] = useState('');
  const [endLocation, setEndLocation] = useState('');
  const [distance, setDistance] = useState('');
  const [rideType, setRideType] = useState('pickup');

  // Data states
  const [rides, setRides] = useState([]);
  const [offers, setOffers] = useState([]);
  const [searchMyRides, setSearchMyRides] = useState('');
  const [searchBrowse, setSearchBrowse] = useState('');

  // Get user's current location
  const getUserLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const { latitude, longitude } = position.coords;
          setUserLocation({
            lat: latitude,
            lon: longitude,
            address: `Lat: ${latitude.toFixed(4)}, Lon: ${longitude.toFixed(4)}`
          });

          // Get address from coordinates
          try {
            const response = await fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${latitude}&lon=${longitude}`);
            const data = await response.json();
            if (data.display_name) {
              setStartLocation(data.display_name.split(',')[0]);
            }
          } catch (error) {
            console.log('Using coordinates as address');
          }
        },
        (error) => {
          console.log('Geolocation error:', error);
          setUserLocation({
            lat: 0,
            lon: 0,
            address: 'Location not available'
          });
        }
      );
    }
  };

  useEffect(() => {
    if (isLoggedIn) {
      getUserLocation();
    }
  }, [isLoggedIn]);

  // Calculate distance between two coordinates
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Earth's radius in km
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = 
      Math.sin(dLat/2) * Math.sin(dLat/2) +
      Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
      Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return (R * c * 0.621371).toFixed(1); // Convert to miles
  };

  const handleLogin = async (type) => {
    if (userName.trim() && userPhone.trim()) {
      try {
        const response = await fetch('http://localhost:5000/api/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ 
            name: userName, 
            phone: userPhone, 
            userType: type 
          })
        });

        const data = await response.json();

        if (data.success) {
          setUserType(type);
          setIsLoggedIn(true);
          setUserId(data.user.id);

          if (type === 'user') {
            loadUserRides();
            loadOffers();
          }
        }
      } catch (error) {
        console.error('Login error:', error);
        // Fallback to local login for demo
        setUserType(type);
        setIsLoggedIn(true);
        setUserId(Date.now().toString());
      }
    }
  };

  const loadUserRides = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/rides/user/${userId}`);
      const data = await response.json();
      if (data.success) setRides(data.rides || []);
    } catch (error) {
      console.log('Loading from localStorage');
      const savedRides = JSON.parse(localStorage.getItem('userRides') || '[]');
      setRides(savedRides.filter(r => r.userId === userId));
    }
  };

  const loadOffers = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/offers');
      const data = await response.json();
      if (data.success) setOffers(data.offers || []);
    } catch (error) {
      const savedOffers = JSON.parse(localStorage.getItem('offers') || '[]');
      setOffers(savedOffers);
    }
  };

  const handleRequestRide = async () => {
    if (startLocation && endLocation) {
      // For demo, generate random distance
      const dist = parseFloat((Math.random() * 20 + 1).toFixed(1));
      const fare = (dist * 2).toFixed(2);

      const rideData = {
        userId,
        userName,
        userPhone,
        startLocation,
        endLocation,
        distance: dist,
        fare,
        rideType,
        claimed: false,
        userLat: userLocation.lat,
        userLon: userLocation.lon
      };

      try {
        const response = await fetch('http://localhost:5000/api/rides', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(rideData)
        });

        if (response.ok) {
          const data = await response.json();
          setRides(prev => [data.ride, ...prev]);
          localStorage.setItem('userRides', JSON.stringify([...rides, data.ride]));

          setStartLocation('');
          setEndLocation('');
          setDistance('');
          alert('Ride requested successfully!');
        }
      } catch (error) {
        // Local storage fallback
        const newRide = { ...rideData, id: Date.now() };
        setRides(prev => [newRide, ...prev]);
        localStorage.setItem('userRides', JSON.stringify([...rides, newRide]));

        setStartLocation('');
        setEndLocation('');
        setDistance('');
        alert('Ride requested (offline mode)!');
      }
    }
  };

  const handleDeleteRide = async (rideId) => {
    try {
      await fetch(`http://localhost:5000/api/rides/${rideId}`, {
        method: 'DELETE',
      });
    } catch (error) {
      console.log('Offline delete');
    }

    setRides(prev => prev.filter(ride => ride._id !== rideId && ride.id !== rideId));
    localStorage.setItem('userRides', JSON.stringify(rides.filter(r => r._id !== rideId && r.id !== rideId)));
  };

  const filterRides = (ridesList, searchTerm) => {
    if (!searchTerm) return ridesList;
    const term = searchTerm.toLowerCase();
    return ridesList.filter(ride => 
      ride.startLocation.toLowerCase().includes(term) ||
      ride.endLocation.toLowerCase().includes(term)
    );
  };

  const filterOffers = (offersList, searchTerm) => {
    if (!searchTerm) return offersList;
    const term = searchTerm.toLowerCase();
    return offersList.filter(offer => 
      offer.startLocation.toLowerCase().includes(term) ||
      offer.endLocation.toLowerCase().includes(term)
    );
  };

  // Login Screen
  if (!isLoggedIn) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-500 to-purple-600 flex items-center justify-center p-4">
        <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-md w-full">
          <div className="text-center mb-8">
            <div className="w-20 h-20 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <Car className="text-blue-500" size={40} />
            </div>
            <h1 className="text-4xl font-bold text-gray-800 mb-2">SyncWay</h1>
            <p className="text-gray-600">Smart Ride Sharing Platform</p>
          </div>

          <div className="space-y-4 mb-6">
            <div className="relative">
              <User className="absolute left-3 top-3 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Your Name"
                value={userName}
                onChange={(e) => setUserName(e.target.value)}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
            <div className="relative">
              <Phone className="absolute left-3 top-3 text-gray-400" size={20} />
              <input
                type="tel"
                placeholder="Phone Number"
                value={userPhone}
                onChange={(e) => setUserPhone(e.target.value)}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <button
              onClick={() => handleLogin('user')}
              className="bg-gradient-to-r from-blue-500 to-blue-600 text-white py-3 rounded-lg font-semibold hover:from-blue-600 hover:to-blue-700 transition flex items-center justify-center gap-2 shadow-lg"
            >
              <User size={20} />
              User
            </button>
            <a
              href="/driver"
              className="bg-gradient-to-r from-purple-500 to-purple-600 text-white py-3 rounded-lg font-semibold hover:from-purple-600 hover:to-purple-700 transition flex items-center justify-center gap-2 shadow-lg"
            >
              <Car size={20} />
              Driver
            </a>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-gradient-to-r from-blue-500 to-blue-600 text-white p-4 shadow-lg">
        <div className="max-w-6xl mx-auto flex justify-between items-center">
          <div className="flex items-center gap-3">
            <div className="bg-white/20 p-2 rounded-lg">
              <Car size={24} />
            </div>
            <div>
              <h1 className="text-2xl font-bold">SyncWay</h1>
              <p className="text-sm opacity-90">Welcome, {userName}</p>
            </div>
          </div>
          <button
            onClick={() => {
              setIsLoggedIn(false);
              setUserType(null);
            }}
            className="bg-white text-blue-600 px-4 py-2 rounded-lg font-semibold hover:bg-gray-100 shadow"
          >
            Logout
          </button>
        </div>
      </div>

      <div className="max-w-6xl mx-auto p-4">
        {/* Tabs */}
        <div className="bg-white rounded-xl shadow-md mb-6 overflow-hidden">
          <div className="flex">
            {['request', 'myrides', 'browse'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`flex-1 py-4 px-6 font-semibold transition-all ${activeTab === tab 
                  ? 'bg-blue-500 text-white' 
                  : 'text-gray-600 hover:bg-gray-50'}`}
              >
                {tab === 'request' && 'Request Ride'}
                {tab === 'myrides' && 'My Rides'}
                {tab === 'browse' && 'Browse Offers'}
              </button>
            ))}
          </div>
        </div>

        {/* Request Ride Tab */}
        {activeTab === 'request' && (
          <div className="bg-white rounded-xl shadow-md p-6">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-2xl font-bold text-gray-800">Request a Ride</h2>
              <button
                onClick={getUserLocation}
                className="flex items-center gap-2 text-blue-500 hover:text-blue-600"
              >
                <Navigation size={20} />
                Use My Location
              </button>
            </div>

            <div className="space-y-6">
              <div className="grid md:grid-cols-2 gap-6">
                <div className="space-y-2">
                  <label className="block text-sm font-medium text-gray-700">Start Location</label>
                  <div className="relative">
                    <MapPin className="absolute left-3 top-3 text-gray-400" size={20} />
                    <input
                      type="text"
                      value={startLocation}
                      onChange={(e) => setStartLocation(e.target.value)}
                      placeholder="Enter pickup location"
                      className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>
                  {userLocation.address && (
                    <p className="text-sm text-gray-500">Current: {userLocation.address}</p>
                  )}
                </div>

                <div className="space-y-2">
                  <label className="block text-sm font-medium text-gray-700">End Location</label>
                  <div className="relative">
                    <MapPin className="absolute left-3 top-3 text-gray-400" size={20} />
                    <input
                      type="text"
                      value={endLocation}
                      onChange={(e) => setEndLocation(e.target.value)}
                      placeholder="Enter destination"
                      className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>
                </div>
              </div>

              {startLocation && endLocation && (
                <div className="bg-blue-50 p-4 rounded-lg">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="font-medium">Estimated Distance</p>
                      <p className="text-2xl font-bold text-blue-600">
                        {calculateDistance(userLocation.lat, userLocation.lon, 
                          userLocation.lat + 0.1, userLocation.lon + 0.1)} miles
                      </p>
                    </div>
                    <div className="text-right">
                      <p className="font-medium">Estimated Fare</p>
                      <p className="text-2xl font-bold text-green-600">
                        ${(calculateDistance(userLocation.lat, userLocation.lon, 
                          userLocation.lat + 0.1, userLocation.lon + 0.1) * 2).toFixed(2)}
                      </p>
                    </div>
                  </div>
                </div>
              )}

              <div className="space-y-2">
                <label className="block text-sm font-medium text-gray-700">Ride Type</label>
                <select
                  value={rideType}
                  onChange={(e) => setRideType(e.target.value)}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="pickup">Standard Pickup</option>
                  <option value="ride">Shared Ride</option>
                  <option value="express">Express Ride</option>
                </select>
              </div>

              <button
                onClick={handleRequestRide}
                className="w-full bg-gradient-to-r from-blue-500 to-blue-600 text-white py-4 rounded-xl font-semibold hover:from-blue-600 hover:to-blue-700 transition text-lg shadow-lg"
              >
                Request Ride Now
              </button>
            </div>
          </div>
        )}

        {/* My Rides Tab */}
        {activeTab === 'myrides' && (
          <div className="bg-white rounded-xl shadow-md p-6">
            <h2 className="text-2xl font-bold text-gray-800 mb-6">My Rides</h2>

            <div className="relative mb-6">
              <Search className="absolute left-3 top-3 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Search your rides..."
                value={searchMyRides}
                onChange={(e) => setSearchMyRides(e.target.value)}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {filterRides(rides.filter(r => r.userId === userId), searchMyRides).length === 0 ? (
              <div className="text-center py-12">
                <div className="w-24 h-24 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Car className="text-gray-400" size={40} />
                </div>
                <h3 className="text-xl font-medium text-gray-600 mb-2">No rides yet</h3>
                <p className="text-gray-500">Request your first ride to get started</p>
              </div>
            ) : (
              <div className="grid md:grid-cols-2 gap-6">
                {filterRides(rides.filter(r => r.userId === userId), searchMyRides).map(ride => (
                  <div key={ride.id || ride._id} className="border border-gray-200 rounded-xl p-5 hover:shadow-lg transition">
                    <div className="flex justify-between items-start mb-4">
                      <div>
                        <h3 className="font-bold text-lg">{ride.startLocation.split(',')[0]} â†’ {ride.endLocation.split(',')[0]}</h3>
                        <div className="flex items-center gap-4 mt-2">
                          <span className="bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm">
                            {ride.distance} miles
                          </span>
                          <span className="bg-green-100 text-green-700 px-3 py-1 rounded-full text-sm font-bold">
                            ${ride.fare}
                          </span>
                        </div>
                      </div>
                      <span className={`px-3 py-1 rounded-full text-sm font-medium ${ride.claimed 
                        ? 'bg-green-100 text-green-700' 
                        : 'bg-yellow-100 text-yellow-700'}`}>
                        {ride.claimed ? 'Claimed âœ“' : 'Waiting'}
                      </span>
                    </div>

                    {ride.claimed && (
                      <div className="mt-4 pt-4 border-t border-gray-200">
                        <div className="flex items-center gap-2 text-gray-700 mb-3">
                          <Phone size={16} />
                          <span className="font-medium">Driver: {ride.claimedBy}</span>
                          <span className="text-gray-500">({ride.claimedDriverPhone})</span>
                        </div>
                        <button className="bg-red-500 text-white px-4 py-2 rounded-lg font-medium hover:bg-red-600 transition">
                          Unclaim Ride
                        </button>
                      </div>
                    )}

                    <div className="mt-4 flex justify-end">
                      <button
                        onClick={() => handleDeleteRide(ride.id || ride._id)}
                        className="text-red-500 hover:text-red-600 flex items-center gap-2"
                      >
                        <X size={16} />
                        Delete Ride
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {/* Browse Offers Tab */}
        {activeTab === 'browse' && (
          <div className="bg-white rounded-xl shadow-md p-6">
            <h2 className="text-2xl font-bold text-gray-800 mb-6">Available Driver Offers</h2>

            <div className="relative mb-6">
              <Search className="absolute left-3 top-3 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Search offers by location..."
                value={searchBrowse}
                onChange={(e) => setSearchBrowse(e.target.value)}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {filterOffers(offers, searchBrowse).length === 0 ? (
              <div className="text-center py-12">
                <div className="w-24 h-24 bg-purple-100 rounded-full flex items-center justify-center mx-auto mb-4">
                  <Car className="text-purple-400" size={40} />
                </div>
                <h3 className="text-xl font-medium text-gray-600 mb-2">No offers available</h3>
                <p className="text-gray-500">Check back later for driver offers</p>
              </div>
            ) : (
              <div className="grid md:grid-cols-2 gap-6">
                {filterOffers(offers, searchBrowse).map(offer => (
                  <div key={offer.id || offer._id} className="border border-purple-200 rounded-xl p-5 bg-purple-50 hover:shadow-lg transition">
                    <div className="flex justify-between items-start mb-4">
                      <div>
                        <h3 className="font-bold text-lg text-purple-800">{offer.startLocation.split(',')[0]} â†’ {offer.endLocation.split(',')[0]}</h3>
                        <div className="flex items-center gap-3 mt-2">
                          <span className="flex items-center gap-1 text-purple-600">
                            <Calendar size={14} />
                            {offer.date}
                          </span>
                          <span className="flex items-center gap-1 text-purple-600">
                            <Clock size={14} />
                            {offer.time}
                          </span>
                        </div>
                      </div>
                      <span className="bg-purple-100 text-purple-700 px-3 py-1 rounded-full text-sm font-medium">
                        Available
                      </span>
                    </div>

                    <div className="flex items-center gap-2 text-gray-700 mt-4 pt-4 border-t border-purple-200">
                      <Phone size={16} />
                      <span className="font-medium">{offer.driverName}</span>
                      <span className="text-gray-500">({offer.driverPhone})</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
