import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import { 
  Car, User, MapPin, Phone, X, Calendar, Clock, Search, 
  Navigation, Mail, Lock, Eye, EyeOff, LogOut, Settings,
  MessageCircle, Star, DollarSign, Filter, Bell, Home
} from 'lucide-react';
import io from 'socket.io-client';


const API_URL = 'http://localhost:5000/api';
const socket = io('http://localhost:5000');

// Login/Register Component
function AuthPage({ onLogin }) {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
    password: '',
    confirmPassword: '',
    userType: 'user'
  });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!isLogin && formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    setLoading(true);

    try {
      const endpoint = isLogin ? '/login' : '/register';
      const payload = isLogin 
        ? { phone: formData.phone, password: formData.password }
        : { 
            name: formData.name, 
            phone: formData.phone, 
            email: formData.email, 
            password: formData.password, 
            userType: formData.userType 
          };

      const response = await fetch(`${API_URL}${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });

      const data = await response.json();

      if (data.success) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        onLogin(data.user, data.token);
      } else {
        setError(data.error || 'Authentication failed');
      }
    } catch (err) {
      setError('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <div className="auth-logo">
            <Car />
          </div>
          <h1 className="auth-title">SyncWay</h1>
          <p className="auth-subtitle">Smart Ride Sharing Platform</p>
        </div>

        <div style={{ marginBottom: '30px', textAlign: 'center' }}>
          <button
            onClick={() => setIsLogin(true)}
            style={{
              background: isLogin ? '#4F46E5' : 'transparent',
              color: isLogin ? 'white' : '#4F46E5',
              padding: '10px 30px',
              border: `2px solid ${isLogin ? '#4F46E5' : '#d1d5db'}`,
              borderRadius: '8px 0 0 8px',
              cursor: 'pointer',
              fontWeight: '600'
            }}
          >
            Login
          </button>
          <button
            onClick={() => setIsLogin(false)}
            style={{
              background: !isLogin ? '#4F46E5' : 'transparent',
              color: !isLogin ? 'white' : '#4F46E5',
              padding: '10px 30px',
              border: `2px solid ${!isLogin ? '#4F46E5' : '#d1d5db'}`,
              borderRadius: '0 8px 8px 0',
              cursor: 'pointer',
              fontWeight: '600',
              marginLeft: '-2px'
            }}
          >
            Register
          </button>
        </div>

        {error && (
          <div style={{
            background: '#FEE2E2',
            color: '#DC2626',
            padding: '12px',
            borderRadius: '8px',
            marginBottom: '20px',
            fontSize: '14px'
          }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <div className="form-group">
              <label className="form-label">Full Name</label>
              <div className="form-input-with-icon">
                <User className="input-icon" />
                <input
                  type="text"
                  className="form-input"
                  placeholder="John Doe"
                  value={formData.name}
                  onChange={(e) => setFormData({...formData, name: e.target.value})}
                  required
                />
              </div>
            </div>
          )}

          {!isLogin && (
            <div className="form-group">
              <label className="form-label">Email Address</label>
              <div className="form-input-with-icon">
                <Mail className="input-icon" />
                <input
                  type="email"
                  className="form-input"
                  placeholder="john@example.com"
                  value={formData.email}
                  onChange={(e) => setFormData({...formData, email: e.target.value})}
                  required
                />
              </div>
            </div>
          )}

          <div className="form-group">
            <label className="form-label">Phone Number</label>
            <div className="form-input-with-icon">
              <Phone className="input-icon" />
              <input
                type="tel"
                className="form-input"
                placeholder="+1 (555) 123-4567"
                value={formData.phone}
                onChange={(e) => setFormData({...formData, phone: e.target.value})}
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label className="form-label">Password</label>
            <div className="form-input-with-icon">
              <Lock className="input-icon" />
              <input
                type={showPassword ? 'text' : 'password'}
                className="form-input"
                placeholder="Enter your password"
                value={formData.password}
                onChange={(e) => setFormData({...formData, password: e.target.value})}
                required
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                style={{
                  position: 'absolute',
                  right: '16px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  color: '#9ca3af'
                }}
              >
                {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
              </button>
            </div>
          </div>

          {!isLogin && (
            <div className="form-group">
              <label className="form-label">Confirm Password</label>
              <div className="form-input-with-icon">
                <Lock className="input-icon" />
                <input
                  type="password"
                  className="form-input"
                  placeholder="Confirm your password"
                  value={formData.confirmPassword}
                  onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})}
                  required
                />
              </div>
            </div>
          )}

          {!isLogin && (
            <div className="form-group">
              <label className="form-label">Account Type</label>
              <div style={{ display: 'flex', gap: '15px' }}>
                <label style={{ flex: 1 }}>
                  <input
                    type="radio"
                    name="userType"
                    value="user"
                    checked={formData.userType === 'user'}
                    onChange={(e) => setFormData({...formData, userType: e.target.value})}
                    style={{ marginRight: '8px' }}
                  />
                  <span>👤 User</span>
                </label>
                <label style={{ flex: 1 }}>
                  <input
                    type="radio"
                    name="userType"
                    value="driver"
                    checked={formData.userType === 'driver'}
                    onChange={(e) => setFormData({...formData, userType: e.target.value})}
                    style={{ marginRight: '8px' }}
                  />
                  <span>🚗 Driver</span>
                </label>
              </div>
            </div>
          )}

          <button 
            type="submit" 
            className="btn btn-primary btn-full"
            disabled={loading}
          >
            {loading ? (
              <span className="loading-spinner"></span>
            ) : (
              <>
                {isLogin ? 'Login' : 'Create Account'}
              </>
            )}
          </button>

          <div style={{ textAlign: 'center', marginTop: '20px' }}>
            <a 
              href={formData.userType === 'user' ? '/driver' : '/'} 
              style={{ color: '#4F46E5', textDecoration: 'none' }}
            >
              {formData.userType === 'user' 
                ? 'Are you a driver? Go to Driver Portal' 
                : 'Are you a user? Go to User Portal'}
            </a>
          </div>
        </form>
      </div>
    </div>
  );
}

// Main App Component
function MainApp() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState('');
  const [activeTab, setActiveTab] = useState('request');

  // Location states
  const [currentLocation, setCurrentLocation] = useState(null);
  const [startLocation, setStartLocation] = useState('');
  const [endLocation, setEndLocation] = useState('');
  const [distance, setDistance] = useState('');
  const [fare, setFare] = useState('');
  const [rideType, setRideType] = useState('standard');

  // Search states
  const [startSuggestions, setStartSuggestions] = useState([]);
  const [endSuggestions, setEndSuggestions] = useState([]);
  const [showStartSuggestions, setShowStartSuggestions] = useState(false);
  const [showEndSuggestions, setShowEndSuggestions] = useState(false);

  // Data states
  const [rides, setRides] = useState([]);
  const [offers, setOffers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [notification, setNotification] = useState(null);

  const navigate = useNavigate();

  // Check if user is logged in
  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');

    if (storedToken && storedUser) {
      setToken(storedToken);
      setUser(JSON.parse(storedUser));

      // Connect to socket
      socket.emit('authenticate', { userId: JSON.parse(storedUser).id });

      // Get current location
      getCurrentLocation();

      // Load initial data
      loadUserRides();
      loadOffers();
    }
  }, []);

  // Socket listeners
  useEffect(() => {
    if (!user) return;

    socket.on('newRideAvailable', (ride) => {
      if (user.userType === 'driver') {
        showNotification('New ride available nearby!', 'info');
      }
    });

    socket.on('driverNearby', (data) => {
      if (user.userType === 'user') {
        showNotification('Driver nearby your pickup location!', 'info');
      }
    });

    return () => {
      socket.off('newRideAvailable');
      socket.off('driverNearby');
    };
  }, [user]);

  const getCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const location = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          };
          setCurrentLocation(location);

          // Update location on server
          if (user) {
            socket.emit('updateLocation', {
              userId: user.id,
              userType: user.userType,
              ...location
            });
          }
        },
        (error) => {
          console.error('Geolocation error:', error);
          showNotification('Unable to get your location', 'error');
        }
      );
    }
  };

  const searchAddress = async (query, type = 'start') => {
    if (!query || query.trim().length < 3) {
      if (type === 'start') {
        setStartSuggestions([]);
        setShowStartSuggestions(false);
      } else {
        setEndSuggestions([]);
        setShowEndSuggestions(false);
      }
      return;
    }

    try {
      const response = await fetch(
        `${API_URL}/places/autocomplete?query=${encodeURIComponent(query)}`
      );
      const data = await response.json();

      if (type === 'start') {
        setStartSuggestions(data.predictions || []);
        setShowStartSuggestions(true);
      } else {
        setEndSuggestions(data.predictions || []);
        setShowEndSuggestions(true);
      }
    } catch (error) {
      console.error('Address search error:', error);
    }
  };

  const handleSelectAddress = async (prediction, type = 'start') => {
    try {
      const response = await fetch(
        `${API_URL}/places/details?placeId=${prediction.place_id}`
      );
      const data = await response.json();

      if (data.success) {
        const place = data.result;

        if (type === 'start') {
          setStartLocation(place.formatted_address);
          setShowStartSuggestions(false);

          // Calculate distance if we have both locations
          if (endLocation) {
            calculateDistance(place.lat, place.lng);
          }
        } else {
          setEndLocation(place.formatted_address);
          setShowEndSuggestions(false);

          // Calculate distance if we have both locations
          if (startLocation && currentLocation) {
            calculateDistance(currentLocation.lat, currentLocation.lng, place.lat, place.lng);
          }
        }
      }
    } catch (error) {
      console.error('Error getting place details:', error);
      showNotification('Failed to get address details', 'error');
    }
  };

  const calculateDistance = async (lat1, lng1, lat2, lng2) => {
    try {
      const response = await fetch(
        `${API_URL}/distance/calculate?lat1=${lat1}&lng1=${lng1}&lat2=${lat2}&lng2=${lng2}`
      );
      const data = await response.json();

      if (data.success) {
        setDistance(data.distance);
        setFare(data.fare);
      }
    } catch (error) {
      console.error('Distance calculation error:', error);
    }
  };

  const loadUserRides = async () => {
    if (!user) return;

    try {
      const response = await fetch(`${API_URL}/rides/user/${user.id}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      const data = await response.json();

      if (data.success) {
        setRides(data.rides || []);
      }
    } catch (error) {
      console.error('Error loading rides:', error);
    }
  };

  const loadOffers = async () => {
    try {
      const response = await fetch(`${API_URL}/offers`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      const data = await response.json();

      if (data.success) {
        setOffers(data.offers || []);
      }
    } catch (error) {
      console.error('Error loading offers:', error);
    }
  };

  const handleRequestRide = async () => {
    if (!startLocation || !endLocation) {
      showNotification('Please select both start and end locations', 'error');
      return;
    }

    setLoading(true);

    try {
      const rideData = {
        startLocation,
        endLocation,
        distance: parseFloat(distance),
        fare: parseFloat(fare),
        rideType,
        startLat: currentLocation?.lat || 0,
        startLng: currentLocation?.lng || 0,
        endLat: 0, // You should get this from place details
        endLng: 0  // You should get this from place details
      };

      const response = await fetch(`${API_URL}/rides`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(rideData)
      });

      const data = await response.json();

      if (data.success) {
        setRides(prev => [data.ride, ...prev]);
        setStartLocation('');
        setEndLocation('');
        setDistance('');
        setFare('');
        showNotification('Ride requested successfully!', 'success');
        setActiveTab('myrides');
      } else {
        showNotification(data.error || 'Failed to request ride', 'error');
      }
    } catch (error) {
      console.error('Error requesting ride:', error);
      showNotification('Network error. Please try again.', 'error');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setToken('');
    navigate('/');
  };

  const showNotification = (message, type = 'info') => {
    setNotification({ message, type });
    setTimeout(() => setNotification(null), 3000);
  };

  if (!user) {
    return <AuthPage onLogin={(userData, token) => {
      setUser(userData);
      setToken(token);
      navigate('/dashboard');
    }} />;
  }

  return (
    <div className="app">
      {/* Notification */}
      {notification && (
        <div className={`notification notification-${notification.type}`}>
          {notification.type === 'success' ? '✅' : 
           notification.type === 'error' ? '❌' : 'ℹ️'}
          {notification.message}
        </div>
      )}

      {/* Header */}
      <header className="main-header">
        <div className="container">
          <div className="header-content">
            <div className="header-brand">
              <div className="header-icon">
                <Car />
              </div>
              <div>
                <h1 className="header-title">SyncWay</h1>
                <p className="header-subtitle">
                  Welcome back, {user.name} ({user.userType})
                </p>
              </div>
            </div>

            <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
              {currentLocation && (
                <button
                  onClick={getCurrentLocation}
                  className="btn btn-outline"
                  style={{ padding: '8px 16px' }}
                >
                  <Navigation size={16} style={{ marginRight: '5px' }} />
                  Update Location
                </button>
              )}

              <button
                onClick={() => navigate('/settings')}
                className="btn btn-outline"
                style={{ padding: '8px 16px' }}
              >
                <Settings size={16} style={{ marginRight: '5px' }} />
                Settings
              </button>

              <button
                onClick={handleLogout}
                className="btn btn-outline"
                style={{ padding: '8px 16px' }}
              >
                <LogOut size={16} style={{ marginRight: '5px' }} />
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <main className="container" style={{ padding: '30px 0' }}>
        {/* Tabs */}
        <div className="tabs-container">
          <nav className="tabs-nav">
            {user.userType === 'user' ? (
              <>
                <button
                  className={`tab-button ${activeTab === 'request' ? 'active' : ''}`}
                  onClick={() => setActiveTab('request')}
                >
                  <Car size={18} style={{ marginRight: '8px' }} />
                  Request Ride
                </button>
                <button
                  className={`tab-button ${activeTab === 'myrides' ? 'active' : ''}`}
                  onClick={() => setActiveTab('myrides')}
                >
                  <Home size={18} style={{ marginRight: '8px' }} />
                  My Rides
                </button>
                <button
                  className={`tab-button ${activeTab === 'offers' ? 'active' : ''}`}
                  onClick={() => setActiveTab('offers')}
                >
                  <Search size={18} style={{ marginRight: '8px' }} />
                  Browse Offers
                </button>
              </>
            ) : (
              <>
                <button
                  className={`tab-button ${activeTab === 'available' ? 'active' : ''}`}
                  onClick={() => setActiveTab('available')}
                >
                  <Car size={18} style={{ marginRight: '8px' }} />
                  Available Rides
                </button>
                <button
                  className={`tab-button ${activeTab === 'myclaims' ? 'active' : ''}`}
                  onClick={() => setActiveTab('myclaims')}
                >
                  <Home size={18} style={{ marginRight: '8px' }} />
                  My Claims
                </button>
                <button
                  className={`tab-button ${activeTab === 'offer' ? 'active' : ''}`}
                  onClick={() => setActiveTab('offer')}
                >
                  <DollarSign size={18} style={{ marginRight: '8px' }} />
                  Offer Ride
                </button>
              </>
            )}
          </nav>
        </div>

        {/* Content based on active tab */}
        {user.userType === 'user' ? (
          <>
            {/* Request Ride Tab */}
            {activeTab === 'request' && (
              <div className="card">
                <div className="card-header">
                  <h2 className="card-title">Request a Ride</h2>
                  <button
                    onClick={getCurrentLocation}
                    className="btn btn-outline"
                    style={{ padding: '10px 20px' }}
                  >
                    <Navigation size={16} style={{ marginRight: '5px' }} />
                    Use My Location
                  </button>
                </div>

                <div className="grid-2">
                  {/* Start Location */}
                  <div className="form-group">
                    <label className="form-label">Start Location</label>
                    <div className="search-container">
                      <div className="form-input-with-icon">
                        <MapPin className="input-icon" />
                        <input
                          type="text"
                          className="form-input"
                          value={startLocation}
                          onChange={(e) => {
                            setStartLocation(e.target.value);
                            searchAddress(e.target.value, 'start');
                          }}
                          onFocus={() => startLocation.length >= 3 && setShowStartSuggestions(true)}
                          placeholder="Enter pickup address..."
                        />
                      </div>

                      {showStartSuggestions && startSuggestions.length > 0 && (
                        <div className="suggestions-dropdown">
                          {startSuggestions.map((prediction) => (
                            <div
                              key={prediction.place_id}
                              className="suggestion-item"
                              onClick={() => handleSelectAddress(prediction, 'start')}
                            >
                              <div className="suggestion-text">
                                {prediction.description}
                              </div>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  </div>

                  {/* End Location */}
                  <div className="form-group">
                    <label className="form-label">End Location</label>
                    <div className="search-container">
                      <div className="form-input-with-icon">
                        <MapPin className="input-icon" />
                        <input
                          type="text"
                          className="form-input"
                          value={endLocation}
                          onChange={(e) => {
                            setEndLocation(e.target.value);
                            searchAddress(e.target.value, 'end');
                          }}
                          onFocus={() => endLocation.length >= 3 && setShowEndSuggestions(true)}
                          placeholder="Enter destination address..."
                        />
                      </div>

                      {showEndSuggestions && endSuggestions.length > 0 && (
                        <div className="suggestions-dropdown">
                          {endSuggestions.map((prediction) => (
                            <div
                              key={prediction.place_id}
                              className="suggestion-item"
                              onClick={() => handleSelectAddress(prediction, 'end')}
                            >
                              <div className="suggestion-text">
                                {prediction.description}
                              </div>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                </div>

                {/* Distance and Fare Display */}
                {distance && fare && (
                  <div className="location-display">
                    <div className="location-distance">
                      <div>
                        <p style={{ fontSize: '14px', color: '#6b7280', marginBottom: '5px' }}>
                          Estimated Distance
                        </p>
                        <p className="distance-value">{distance} miles</p>
                      </div>
                      <div>
                        <p style={{ fontSize: '14px', color: '#6b7280', marginBottom: '5px' }}>
                          Estimated Fare
                        </p>
                        <p className="fare-value">${fare}</p>
                      </div>
                    </div>
                    <p className="location-route">
                      {startLocation.split(',')[0]} → {endLocation.split(',')[0]}
                    </p>
                  </div>
                )}

                {/* Ride Type */}
                <div className="form-group">
                  <label className="form-label">Ride Type</label>
                  <select
                    className="form-input"
                    value={rideType}
                    onChange={(e) => setRideType(e.target.value)}
                  >
                    <option value="standard">Standard Ride</option>
                    <option value="shared">Shared Ride</option>
                    <option value="express">Express Ride</option>
                    <option value="luxury">Luxury Ride</option>
                  </select>
                </div>

                {/* Request Button */}
                <button
                  onClick={handleRequestRide}
                  disabled={!startLocation || !endLocation || loading}
                  className="btn btn-primary btn-full"
                  style={{ padding: '16px', fontSize: '18px' }}
                >
                  {loading ? (
                    <span className="loading-spinner"></span>
                  ) : !startLocation || !endLocation ? (
                    'Select Both Locations First'
                  ) : (
                    'Request Ride Now'
                  )}
                </button>
              </div>
            )}

            {/* My Rides Tab */}
            {activeTab === 'myrides' && (
              <div className="card">
                <div className="card-header">
                  <h2 className="card-title">My Rides</h2>
                  <div className="search-container" style={{ width: '300px' }}>
                    <Search className="search-icon" />
                    <input
                      type="text"
                      className="search-input"
                      placeholder="Search your rides..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                    />
                  </div>
                </div>

                {rides.length === 0 ? (
                  <div className="empty-state">
                    <div className="empty-icon">
                      <Car />
                    </div>
                    <h3 className="empty-title">No rides yet</h3>
                    <p className="empty-message">
                      Request your first ride to get started
                    </p>
                    <button
                      onClick={() => setActiveTab('request')}
                      className="btn btn-primary"
                      style={{ marginTop: '20px' }}
                    >
                      Request a Ride
                    </button>
                  </div>
                ) : (
                  <div className="grid-2">
