        ) : (
          // Driver Interface
          <>
            {/* Available Rides Tab */}
            {activeTab === 'available' && (
              <div className="card">
                <div className="card-header">
                  <h2 className="card-title">Available Rides Near You</h2>
                  <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
                    <div className="search-container" style={{ width: '250px' }}>
                      <Search className="search-icon" />
                      <input
                        type="text"
                        className="search-input"
                        placeholder="Search rides..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                      />
                    </div>
                    <button
                      onClick={getCurrentLocation}
                      className="btn btn-outline"
                      style={{ padding: '10px 20px' }}
                    >
                      <Navigation size={16} style={{ marginRight: '5px' }} />
                      Refresh
                    </button>
                  </div>
                </div>

                {rides.length === 0 ? (
                  <div className="empty-state">
                    <div className="empty-icon">
                      <Car />
                    </div>
                    <h3 className="empty-title">No rides available</h3>
                    <p className="empty-message">
                      Check back later for ride requests
                    </p>
                  </div>
                ) : (
                  <div className="grid-2">
                    {rides
                      .filter(ride => 
                        ride.startLocation.toLowerCase().includes(searchTerm.toLowerCase()) ||
                        ride.endLocation.toLowerCase().includes(searchTerm.toLowerCase())
                      )
                      .map((ride) => (
                        <div key={ride._id} className="ride-card available">
                          <div className="ride-route">
                            {ride.startLocation.split(',')[0]} → {ride.endLocation.split(',')[0]}
                          </div>

                          <div className="ride-details">
                            <span className="ride-badge badge-distance">
                              {ride.distance || 0} miles
                            </span>
                            <span className="ride-badge badge-fare">
                              ${ride.fare || 0}
                            </span>
                            {ride.distanceFromUser && (
                              <span className="ride-badge" style={{ background: '#fce7f3', color: '#be185d' }}>
                                {ride.distanceFromUser.toFixed(1)} miles away
                              </span>
                            )}
                          </div>

                          <div className="ride-info">
                            <User size={16} />
                            <span>Passenger: {ride.userName || 'Anonymous'}</span>
                          </div>

                          <div className="ride-info">
                            <Star size={16} />
                            <span>Rating: {ride.userRating || 'New user'}</span>
                          </div>

                          <div className="ride-actions">
                            <span style={{ fontSize: '14px', color: '#6b7280' }}>
                              Requested {new Date(ride.createdAt).toLocaleDateString()}
                            </span>
                            <button
                              className="btn btn-primary"
                              style={{ padding: '8px 16px' }}
                              onClick={async () => {
                                try {
                                  const response = await fetch(`${API_URL}/rides/${ride._id}/claim`, {
                                    method: 'PUT',
                                    headers: {
                                      'Content-Type': 'application/json',
                                      'Authorization': `Bearer ${token}`
                                    }
                                  });

                                  const data = await response.json();
                                  if (data.success) {
                                    showNotification('Ride claimed successfully!', 'success');
                                    setRides(prev => prev.filter(r => r._id !== ride._id));
                                  }
                                } catch (error) {
                                  showNotification('Failed to claim ride', 'error');
                                }
                              }}
                            >
                              Claim This Ride
                            </button>
                          </div>
