                <div className="card-header">
                  <h2 className="card-title">Your Claimed Rides</h2>
                  <div className="search-container" style={{ width: '300px' }}>
                    <Search className="search-icon" />
                    <input
                      type="text"
                      className="search-input"
                      placeholder="Search your claims..."
                      value={searchTerm}
