}

// Settings Component
function SettingsPage() {
  const [user, setUser] = useState(null);
  const [notifications, setNotifications] = useState(true);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
      setNotifications(JSON.parse(storedUser).notificationsEnabled || true);
    }
  }, []);

  const handleSave = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`${API_URL}/user/profile`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ notificationsEnabled: notifications })
      });

      const data = await response.json();
      if (data.success) {
        localStorage.setItem('user', JSON.stringify(data.user));
        alert('Settings saved successfully!');
      }
    } catch (error) {
      alert('Failed to save settings');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteAccount = async () => {
    if (window.confirm('Are you sure you want to delete your account? This action cannot be undone.')) {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`${API_URL}/user/delete`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        const data = await response.json();
        if (data.success) {
          localStorage.clear();
          window.location.href = '/';
        }
      } catch (error) {
        alert('Failed to delete account');
      }
    }
  };

  if (!user) return null;

  return (
    <div className="app">
      <header className="main-header">
        <div className="container">
          <div className="header-content">
            <div className="header-brand">
              <div className="header-icon">
                <Settings />
              </div>
              <div>
                <h1 className="header-title">Settings</h1>
                <p className="header-subtitle">Manage your account preferences</p>
              </div>
            </div>

            <button
              onClick={() => window.history.back()}
              className="btn btn-outline"
              style={{ padding: '10px 20px', color: 'white', borderColor: 'white' }}
            >
              ← Back
            </button>
          </div>
        </div>
      </header>

      <main className="container" style={{ padding: '30px 0' }}>
        <div className="card">
          <h2 className="card-title" style={{ marginBottom: '30px' }}>
            Account Settings
          </h2>

          <div className="form-group">
            <label className="form-label">Email Notifications</label>
            <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
              <button
                onClick={() => setNotifications(true)}
                style={{
                  background: notifications ? '#4F46E5' : '#f3f4f6',
                  color: notifications ? 'white' : '#6b7280',
                  padding: '10px 20px',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontWeight: '500'
                }}
              >
                Enabled
              </button>
              <button
                onClick={() => setNotifications(false)}
                style={{
                  background: !notifications ? '#4F46E5' : '#f3f4f6',
                  color: !notifications ? 'white' : '#6b7280',
                  padding: '10px 20px',
                  border: 'none',
                  borderRadius: '8px',
                  cursor: 'pointer',
                  fontWeight: '500'
                }}
              >
                Disabled
              </button>
            </div>
            <p style={{ fontSize: '14px', color: '#6b7280', marginTop: '10px' }}>
              When enabled, you'll receive email notifications for new rides, ride claims, and updates.
            </p>
          </div>

          <div style={{ marginTop: '40px', paddingTop: '20px', borderTop: '1px solid #e5e7eb' }}>
            <h3 style={{ fontSize: '18px', fontWeight: '600', marginBottom: '20px' }}>
              Account Information
            </h3>
            <div style={{ background: '#f9fafb', padding: '20px', borderRadius: '8px' }}>
              <p><strong>Name:</strong> {user.name}</p>
              <p><strong>Email:</strong> {user.email}</p>
              <p><strong>Phone:</strong> {user.phone}</p>
              <p><strong>Account Type:</strong> {user.userType}</p>
              <p><strong>Member Since:</strong> {new Date(user.createdAt).toLocaleDateString()}</p>
            </div>
          </div>

          <div style={{ marginTop: '40px', paddingTop: '20px', borderTop: '1px solid #e5e7eb' }}>
            <h3 style={{ fontSize: '18px', fontWeight: '600', marginBottom: '20px', color: '#ef4444' }}>
              Danger Zone
            </h3>
            <div style={{ background: '#fef2f2', padding: '20px', borderRadius: '8px' }}>
              <p style={{ marginBottom: '15px' }}>
                Deleting your account will remove all your data and cancel all active rides.
                <strong> This action cannot be undone.</strong>
              </p>
              <button
                onClick={handleDeleteAccount}
                className="btn btn-danger"
                style={{ padding: '12px 24px' }}
              >
                Delete My Account
              </button>
            </div>
          </div>

          <div style={{ marginTop: '40px', display: 'flex', justifyContent: 'flex-end', gap: '15px' }}>
            <button
              onClick={() => window.history.back()}
              className="btn btn-outline"
              style={{ padding: '12px 24px' }}
            >
              Cancel
            </button>
            <button
              onClick={handleSave}
              disabled={loading}
              className="btn btn-primary"
              style={{ padding: '12px 24px' }}
            >
              {loading ? 'Saving...' : 'Save Changes'}
            </button>
          </div>
        </div>
      </main>
    </div>
  );
}

// Main Router Component
function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<MainApp />} />
        <Route path="/dashboard" element={<MainApp />} />
        <Route path="/settings" element={<SettingsPage />} />
        <Route path="/driver" element={<MainApp />} />
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
